import sys
import math

def solve() -> None:
    data = list(map(int, sys.stdin.buffer.read().split()))
    it = iter(data)
    t = next(it)
    out_lines = []
    for _ in range(t):
        n = next(it)
        a = [next(it) for _ in range(n)]
        pref = [0] * (n + 1)
        for i in range(n):
            pref[i + 1] = pref[i] + a[i]

        best = 0  # at least 0 (k = n)
        # generate divisors
        divs = []
        limit = int(math.isqrt(n))
        for d in range(1, limit + 1):
            if n % d == 0:
                divs.append(d)
                if d != n // d:
                    divs.append(n // d)

        for k in divs:
            if k == n:
                continue  # diff = 0, already accounted
            max_sum = -1
            min_sum = 10**30
            blocks = n // k
            for i in range(blocks):
                s = pref[(i + 1) * k] - pref[i * k]
                if s > max_sum:
                    max_sum = s
                if s < min_sum:
                    min_sum = s
            diff = max_sum - min_sum
            if diff > best:
                best = diff
        out_lines.append(str(best))
    sys.stdout.write("\n".join(out_lines))

if __name__ == "__main__":
    solve()
```

