class Solution:
    def punishmentNumber(self, n: int) -> int:
        from functools import lru_cache

        def can_partition(num: int) -> bool:
            s = str(num * num)
            target = num
            L = len(s)

            @lru_cache(None)
            def dfs(idx: int, cur: int) -> bool:
                if idx == L:
                    return cur == target
                if cur > target:
                    return False
                val = 0
                for j in range(idx, L):
                    val = val * 10 + (ord(s[j]) - 48)   # build integer value
                    if cur + val > target and val != 0:
                        break
                    if dfs(j + 1, cur + val):
                        return True
                return False

            return dfs(0, 0)

        total = 0
        for i in range(1, n + 1):
            if can_partition(i):
                total += i * i
        return total
```

