from typing import List

class Solution:
    def paintWalls(self, cost: List[int], time: List[int]) -> int:
        n = len(cost)
        INF = 10**18
        # dp[v] = minimum cost to achieve total value >= v, where value = time[i] + 1
        dp = [INF] * (n + 1)
        dp[0] = 0

        for c, t in zip(cost, time):
            val = t + 1
            # iterate backwards to avoid reuse of the same wall
            for v in range(n, -1, -1):
                if dp[v] == INF:
                    continue
                nv = v + val
                if nv > n:
                    nv = n
                if dp[nv] > dp[v] + c:
                    dp[nv] = dp[v] + c

        return dp[n]
```

