from typing import List

MOD = 10**9 + 7

class Solution:
    def specialPerm(self, nums: List[int]) -> int:
        n = len(nums)
        # adjacency matrix: can go from i to j
        can = [[False] * n for _ in range(n)]
        for i in range(n):
            for j in range(n):
                if i == j:
                    continue
                a, b = nums[i], nums[j]
                if a % b == 0 or b % a == 0:
                    can[i][j] = True

        size = 1 << n
        dp = [[0] * n for _ in range(size)]

        for i in range(n):
            dp[1 << i][i] = 1

        for mask in range(size):
            for last in range(n):
                cur = dp[mask][last]
                if cur == 0:
                    continue
                # try to add a new element nxt
                nxt_mask = (~mask) & (size - 1)  # bits not used yet
                while nxt_mask:
                    nxt = (nxt_mask & -nxt_mask).bit_length() - 1
                    nxt_mask &= nxt_mask - 1
                    if can[last][nxt]:
                        dp[mask | (1 << nxt)][nxt] = (dp[mask | (1 << nxt)][nxt] + cur) % MOD

        full = size - 1
        ans = sum(dp[full][i] for i in range(n)) % MOD
        return ans
```

