from typing import List

class Solution:
    def maximumOr(self, nums: List[int], k: int) -> int:
        n = len(nums)
        # prefix and suffix OR arrays
        pref = [0] * (n + 1)
        for i in range(n):
            pref[i + 1] = pref[i] | nums[i]
        suff = [0] * (n + 1)
        for i in range(n - 1, -1, -1):
            suff[i] = suff[i + 1] | nums[i]

        ans = 0
        for i in range(n):
            others_or = pref[i] | suff[i + 1]
            val = nums[i]
            # try using t operations on this element, 0 <= t <= k
            for t in range(k + 1):
                candidate = (val << t) | others_or
                if candidate > ans:
                    ans = candidate
        return ans
```

