from typing import List

class Solution:
    def minimizeConcatenatedLength(self, words: List[str]) -> int:
        if not words:
            return 0
        # initial state from the first word
        dp = {(words[0][0], words[0][-1]): len(words[0])}
        for w in words[1:]:
            w_len = len(w)
            w_first, w_last = w[0], w[-1]
            new_dp = {}
            for (f, l), cur_len in dp.items():
                # option 1: str + w
                merged_len1 = cur_len + w_len - (1 if l == w_first else 0)
                key1 = (f, w_last)
                if key1 not in new_dp or merged_len1 < new_dp[key1]:
                    new_dp[key1] = merged_len1
                # option 2: w + str
                merged_len2 = w_len + cur_len - (1 if w_last == f else 0)
                key2 = (w_first, l)
                if key2 not in new_dp or merged_len2 < new_dp[key2]:
                    new_dp[key2] = merged_len2
            dp = new_dp
        return min(dp.values())
