class Solution:
    def sortVowels(self, s: str) -> str:
        vowels = set('aeiouAEIOU')
        # Extract vowels and sort them by ASCII value
        vowel_chars = sorted([c for c in s if c in vowels])
        # Reconstruct the string, placing sorted vowels in original vowel positions
        res = []
        idx = 0  # index in vowel_chars
        for ch in s:
            if ch in vowels:
                res.append(vowel_chars[idx])
                idx += 1
            else:
                res.append(ch)
        return ''.join(res)
