from typing import List

class Solution:
    def alternatingSubarray(self, nums: List[int]) -> int:
        n = len(nums)
        max_len = -1
        cur_len = 0  # length of current alternating subarray ending at previous index

        for i in range(1, n):
            diff = nums[i] - nums[i - 1]

            if cur_len == 0:
                # no active subarray, can only start if diff is +1
                cur_len = 2 if diff == 1 else 0
            else:
                # expected diff depends on parity of current length
                expected = 1 if cur_len % 2 == 1 else -1
                if diff == expected:
                    cur_len += 1
                else:
                    # try to start a new subarray with this pair
                    cur_len = 2 if diff == 1 else 0

            if cur_len >= 2:
                max_len = max(max_len, cur_len)

        return max_len
