class Solution:
    def minimumBeautifulSubstrings(self, s: str) -> int:
        n = len(s)
        # Precompute binary strings of powers of 5 up to length 15
        power_set = set()
        val = 1
        while True:
            b = bin(val)[2:]
            if len(b) > 15:
                break
            power_set.add(b)
            val *= 5

        INF = float('inf')
        dp = [INF] * (n + 1)
        dp[0] = 0

        for i in range(n):
            if dp[i] == INF:
                continue
            # substrings starting at i
            for j in range(i + 1, n + 1):
                sub = s[i:j]
                # leading zero check
                if sub[0] == '0':
                    continue
                if sub in power_set:
                    dp[j] = min(dp[j], dp[i] + 1)

        return -1 if dp[n] == INF else dp[n]
```

