from collections import Counter

class Solution:
    def isGood(self, nums: List[int]) -> bool:
        if not nums:
            return False

        n = max(nums)                     # candidate n
        if len(nums) != n + 1:            # length must be n+1
            return False

        cnt = Counter(nums)

        # there must be no numbers outside 1..n
        if any(k < 1 or k > n for k in cnt):
            return False

        # check counts for each required number
        for i in range(1, n):
            if cnt.get(i, 0) != 1:
                return False

        if cnt.get(n, 0) != 2:
            return False

        return True
