from typing import List

class Solution:
    def maxSum(self, nums: List[int]) -> int:
        # Helper to compute maximum digit of a number
        def max_digit(x: int) -> int:
            m = 0
            while x:
                d = x % 10
                if d > m:
                    m = d
                    if m == 9:   # early exit, can't get higher
                        break
                x //= 10
            return m

        # Store the two largest numbers for each possible max digit (0-9)
        top = {d: [] for d in range(10)}  # list will keep at most two largest values

        for num in nums:
            d = max_digit(num)
            lst = top[d]
            # Insert while keeping list sorted descending, size <= 2
            if not lst:
                lst.append(num)
            elif len(lst) == 1:
                if num >= lst[0]:
                    lst.insert(0, num)
                else:
                    lst.append(num)
            else:  # len == 2
                if num > lst[0]:
                    lst[1] = lst[0]
                    lst[0] = num
                elif num > lst[1]:
                    lst[1] = num
            # Ensure we never keep more than two elements
            if len(lst) > 2:
                lst.pop()

        max_sum = -1
        for lst in top.values():
            if len(lst) == 2:
                cur = lst[0] + lst[1]
                if cur > max_sum:
                    max_sum = cur
        return max_sum
```

