from typing import List

class Solution:
    def canSplitArray(self, nums: List[int], m: int) -> bool:
        n = len(nums)
        pref = [0] * (n + 1)
        for i in range(n):
            pref[i + 1] = pref[i] + nums[i]

        def seg_sum(l: int, r: int) -> int:
            return pref[r + 1] - pref[l]

        dp = [[False] * n for _ in range(n)]

        # length 1 subarrays are always splittable (already singletons)
        for i in range(n):
            dp[i][i] = True

        # consider subarrays of increasing length
        for length in range(2, n + 1):
            for l in range(0, n - length + 1):
                r = l + length - 1
                # try every possible split point
                for k in range(l, r):
                    left_len = k - l + 1
                    right_len = r - k
                    left_ok = (left_len == 1) or (seg_sum(l, k) >= m)
                    right_ok = (right_len == 1) or (seg_sum(k + 1, r) >= m)
                    if left_ok and right_ok and dp[l][k] and dp[k + 1][r]:
                        dp[l][r] = True
                        break  # no need to check further splits

        return dp[0][n - 1]
```

