from typing import List

class Solution:
    def maxArrayValue(self, nums: List[int]) -> int:
        # Greedy from right to left.
        cur = nums[-1]
        ans = cur
        for i in range(len(nums) - 2, -1, -1):
            if nums[i] <= cur:
                cur += nums[i]          # merge into the current group
            else:
                cur = nums[i]           # start a new group
            if cur > ans:
                ans = cur
        return ans
