from itertools import permutations

class Solution:
    def minimumString(self, a: str, b: str, c: str) -> str:
        def merge(x: str, y: str) -> str:
            # if one already contains the other
            if y in x:
                return x
            if x in y:
                return y
            # maximal suffix/prefix overlap (x then y)
            max_olap = 0
            max_len = min(len(x), len(y))
            for k in range(1, max_len + 1):
                if x[-k:] == y[:k]:
                    max_olap = k
            return x + y[max_olap:]

        strings = [a, b, c]
        best = None
        for perm in permutations(strings):
            cur = perm[0]
            cur = merge(cur, perm[1])
            cur = merge(cur, perm[2])
            if best is None or len(cur) < len(best) or (len(cur) == len(best) and cur < best):
                best = cur
        return best
```

