class Solution:
    def canMakeSubsequence(self, str1: str, str2: str) -> bool:
        n, m = len(str1), len(str2)
        if m > n:
            return False

        j = 0
        for i in range(n):
            if j == m:
                break
            c = str1[i]
            nxt = 'a' if c == 'z' else chr(ord(c) + 1)
            if str2[j] == c or str2[j] == nxt:
                j += 1

        return j == m
