from typing import List
from collections import defaultdict

class Solution:
    def minimumSeconds(self, nums: List[int]) -> int:
        n = len(nums)
        pos_dict = defaultdict(list)
        for i, v in enumerate(nums):
            pos_dict[v].append(i)

        ans = n  # upper bound
        for positions in pos_dict.values():
            # positions are already in increasing order because we filled them sequentially
            max_gap = 0
            for i in range(len(positions) - 1):
                gap = positions[i + 1] - positions[i]
                if gap > max_gap:
                    max_gap = gap
            # wraparound gap
            wrap_gap = positions[0] + n - positions[-1]
            if wrap_gap > max_gap:
                max_gap = wrap_gap

            t = max_gap // 2
            if t < ans:
                ans = t

        return ans
```

