from functools import lru_cache

class Solution:
    def numberOfBeautifulIntegers(self, low: int, high: int, k: int) -> int:
        def count_upto(N: int) -> int:
            if N <= 0:
                return 0
            digits = list(map(int, str(N)))
            n = len(digits)
            max_diff = n  # max possible absolute diff

            @lru_cache(None)
            def dp(pos: int, diff: int, rem: int, started: bool, parity: int, tight: bool) -> int:
                if pos == n:
                    # valid only if we have started, even length, diff zero and divisible by k
                    return int(started and parity == 0 and diff == 0 and rem == 0)
                limit = digits[pos] if tight else 9
                total = 0
                for d in range(0, limit + 1):
                    ntight = tight and (d == limit)
                    nstarted = started or d != 0
                    ndiff = diff
                    nrem = rem
                    nparity = parity
                    if nstarted:
                        # update parity (length modulo 2)
                        nparity ^= 1
                        # update diff based on parity of digit
                        if d % 2 == 0:
                            ndiff += 1
                        else:
                            ndiff -= 1
                        # update remainder
                        nrem = (rem * 10 + d) % k
                    total += dp(pos + 1, ndiff, nrem, nstarted, nparity, ntight)
                return total

            return dp(0, 0, 0, False, 0, True)

        return count_upto(high) - count_upto(low - 1)
```

