import sys
from functools import lru_cache

MOD = 10**9 + 7

class Solution:
    def countSteppingNumbers(self, low: str, high: str) -> int:
        sys.setrecursionlimit(10000)

        def dec_str(num: str) -> str:
            if num == "0":
                return "0"
            lst = list(num)
            i = len(lst) - 1
            while i >= 0 and lst[i] == '0':
                lst[i] = '9'
                i -= 1
            lst[i] = str(int(lst[i]) - 1)
            res = ''.join(lst).lstrip('0')
            return res if res else "0"

        def count_up_to(s: str) -> int:
            n = len(s)

            @lru_cache(None)
            def dp(pos: int, tight: int, prev: int, started: int) -> int:
                if pos == n:
                    return 1 if started else 0  # count only if a number has started
                limit = int(s[pos]) if tight else 9
                total = 0
                for d in range(0, limit + 1):
                    ntight = tight and (d == limit)
                    nstarted = started or (d != 0)
                    if not nstarted:
                        # still leading zeros, prev stays -1
                        total += dp(pos + 1, ntight, -1, 0)
                    else:
                        if not started:
                            # first nonzero digit, any d (1..9) allowed
                            total += dp(pos + 1, ntight, d, 1)
                        else:
                            # already started, need |d - prev| == 1
                            if abs(d - prev) == 1:
                                total += dp(pos + 1, ntight, d, 1)
                return total % MOD

            return dp(0, 1, -1, 0)

        high_cnt = count_up_to(high)
        low_minus_one = dec_str(low)
        low_cnt = count_up_to(low_minus_one)
        return (high_cnt - low_cnt) % MOD
```

