from collections import deque

class Solution:
    def longestString(self, x: int, y: int, z: int) -> int:
        # piece types: 1 = "AA", 2 = "BB", 3 = "AB"
        max_a, max_b, max_c = x, y, z

        # visited[a][b][c][last]  last: 0 = start, 1 = AA, 2 = BB, 3 = AB
        visited = [[[[False] * 4 for _ in range(max_c + 1)]
                    for _ in range(max_b + 1)] for _ in range(max_a + 1)]

        q = deque()
        visited[0][0][0][0] = True
        q.append((0, 0, 0, 0))

        while q:
            a, b, c, last = q.popleft()

            # try to add "AA"
            if a < max_a and (last == 0 or last in (2, 3)):
                if not visited[a + 1][b][c][1]:
                    visited[a + 1][b][c][1] = True
                    q.append((a + 1, b, c, 1))

            # try to add "BB"
            if b < max_b and (last == 0 or last == 1):
                if not visited[a][b + 1][c][2]:
                    visited[a][b + 1][c][2] = True
                    q.append((a, b + 1, c, 2))

            # try to add "AB"
            if c < max_c and (last == 0 or last in (2, 3)):
                if not visited[a][b][c + 1][3]:
                    visited[a][b][c + 1][3] = True
                    q.append((a, b, c + 1, 3))

        max_pieces = 0
        for a in range(max_a + 1):
            for b in range(max_b + 1):
                for c in range(max_c + 1):
                    if any(visited[a][b][c][t] for t in range(4)):
                        max_pieces = max(max_pieces, a + b + c)

        return max_pieces * 2
