class Solution:
    def minimumChanges(self, s: str, k: int) -> int:
        n = len(s)
        INF = 10 ** 9

        # precompute proper divisors for each length
        divisors = [[] for _ in range(n + 1)]
        for L in range(2, n + 1):
            for d in range(1, L):
                if L % d == 0:
                    divisors[L].append(d)

        # cost[i][j]: min changes to make s[i:j] a semipalindrome (j exclusive)
        cost = [[INF] * (n + 1) for _ in range(n)]
        for i in range(n):
            for j in range(i + 1, n + 1):
                L = j - i
                if L == 1:          # length 1 can never be semipalindrome
                    cost[i][j] = INF
                    continue
                best = INF
                for d in divisors[L]:
                    cur = 0
                    group_len = L // d
                    for r in range(d):
                        left, right = 0, group_len - 1
                        while left < right:
                            c1 = s[i + r + left * d]
                            c2 = s[i + r + right * d]
                            if c1 != c2:
                                cur += 1
                            left += 1
                            right -= 1
                    if cur < best:
                        best = cur
                cost[i][j] = best

        # dp[p][i]: min changes to partition first i chars into p substrings
        dp = [[INF] * (n + 1) for _ in range(k + 1)]
        dp[0][0] = 0
        for p in range(1, k + 1):
            for i in range(1, n + 1):
                # previous cut position t must leave at least (p-1) chars for earlier parts
                for t in range(p - 1, i):
                    if dp[p - 1][t] == INF or cost[t][i] == INF:
                        continue
                    val = dp[p - 1][t] + cost[t][i]
                    if val < dp[p][i]:
                        dp[p][i] = val

        return dp[k][n]
```

