from typing import List
from collections import defaultdict
import math

class Solution:
    def maximumSum(self, nums: List[int]) -> int:
        # generate primes up to sqrt(1e9)
        limit = int(math.isqrt(10**9)) + 1
        sieve = [True] * (limit + 1)
        primes = []
        for i in range(2, limit + 1):
            if sieve[i]:
                primes.append(i)
                step = i
                start = i * i
                if start <= limit:
                    for j in range(start, limit + 1, step):
                        sieve[j] = False

        group_sum = defaultdict(int)

        for num in nums:
            x = num
            sf = 1  # squarefree part
            for p in primes:
                if p * p > x:
                    break
                cnt = 0
                while x % p == 0:
                    x //= p
                    cnt ^= 1          # toggle parity
                if cnt:
                    sf *= p
            if x > 1:                # remaining prime factor with odd exponent (1)
                sf *= x
            group_sum[sf] += num

        return max(group_sum.values())
```

