class Solution:
    def minimumOperations(self, num: str) -> int:
        n = len(num)
        ans = n  # worst case: delete all digits

        # patterns for numbers divisible by 25
        targets = ["00", "25", "50", "75"]
        for t in targets:
            # find rightmost occurrence of the second digit
            pos2 = -1
            for i in range(n - 1, -1, -1):
                if num[i] == t[1]:
                    pos2 = i
                    break
            if pos2 == -1:
                continue
            # find occurrence of the first digit to the left of pos2
            pos1 = -1
            for i in range(pos2 - 1, -1, -1):
                if num[i] == t[0]:
                    pos1 = i
                    break
            if pos1 == -1:
                continue
            # deletions needed to bring these two digits to the end
            deletions = n - pos1 - 2
            ans = min(ans, deletions)

        # consider making the number "0" by keeping a single zero
        if '0' in num:
            ans = min(ans, n - 1)

        return ans
```

