class Solution:
    def minimumSum(self, nums: List[int]) -> int:
        n = len(nums)
        INF = float('inf')
        ans = INF

        # Precompute for each j the minimal left element smaller than nums[j]
        left_min = [INF] * n
        for j in range(1, n):
            best = INF
            for i in range(j):
                if nums[i] < nums[j]:
                    best = min(best, nums[i])
            left_min[j] = best

        # For each j, find minimal right element smaller than nums[j] and compute sum
        for j in range(1, n - 1):
            if left_min[j] == INF:
                continue
            right_best = INF
            for k in range(j + 1, n):
                if nums[k] < nums[j]:
                    right_best = min(right_best, nums[k])
            if right_best != INF:
                ans = min(ans, left_min[j] + nums[j] + right_best)

        return -1 if ans == INF else ans
```

