from typing import List
import bisect

class FenwickMax:
    def __init__(self, n: int):
        self.n = n
        self.bit = [-10**30] * (n + 1)  # very small sentinel

    def update(self, idx: int, value: int):
        i = idx + 1  # 1based
        while i <= self.n:
            if value > self.bit[i]:
                self.bit[i] = value
            i += i & -i

    def query(self, idx: int) -> int:
        """maximum on prefix [0, idx] (inclusive)"""
        i = idx + 1
        res = -10**30
        while i > 0:
            if self.bit[i] > res:
                res = self.bit[i]
            i -= i & -i
        return res


class Solution:
    def maxBalancedSubsequenceSum(self, nums: List[int]) -> int:
        n = len(nums)
        keys = [nums[i] - i for i in range(n)]
        uniq = sorted(set(keys))
        comp = {v: i for i, v in enumerate(uniq)}
        ft = FenwickMax(len(uniq))

        best_overall = -10**30
        for i in range(n):
            k = comp[keys[i]]
            prev_max = ft.query(k)          # max DP for keys <= current
            prev_max = max(prev_max, 0)     # we may start new subsequence
            cur = nums[i] + prev_max
            ft.update(k, cur)
            if cur > best_overall:
                best_overall = cur
        return best_overall
```

