class Solution:
    def minSum(self, nums1: List[int], nums2: List[int]) -> int:
        sum1 = 0
        zeros1 = 0
        for v in nums1:
            if v == 0:
                zeros1 += 1
            else:
                sum1 += v

        sum2 = 0
        zeros2 = 0
        for v in nums2:
            if v == 0:
                zeros2 += 1
            else:
                sum2 += v

        base1 = sum1 + zeros1          # minimal possible sum of nums1 (all zeros become 1)
        base2 = sum2 + zeros2          # minimal possible sum of nums2

        if base1 == base2:
            return base1

        if base1 < base2:
            # need to increase nums1; possible only if it has at least one zero
            return base2 if zeros1 > 0 else -1
        else:  # base2 < base1
            # need to increase nums2; possible only if it has at least one zero
            return base1 if zeros2 > 0 else -1
