        n = len(nums)
        # last occurrence of each value
        last = {}
        for i, v in enumerate(nums):
            last[v] = i

        cnt = 0
        cur_max = -1
        for i in range(n - 1):          # only consider cuts before the last element
            cur_max = max(cur_max, last[nums[i]])
            if i == cur_max:
                cnt += 1

        return pow(2, cnt, MOD)
