class Solution:
    def findMissingAndRepeatedValues(self, grid: List[List[int]]) -> List[int]:
        n = len(grid)
        total = n * n
        seen = [False] * (total + 1)
        repeated = -1

        for row in grid:
            for val in row:
                if seen[val]:
                    repeated = val
                else:
                    seen[val] = True

        missing = -1
        for num in range(1, total + 1):
            if not seen[num]:
                missing = num
                break

        return [repeated, missing]
