class Solution:
    def removeAlmostEqualCharacters(self, word: str) -> int:
        n = len(word)
        INF = 10 ** 9
        # dp[c] = min changes up to current position with character c at this position
        dp = [INF] * 26
        # initialize for first character
        for c in range(26):
            dp[c] = 0 if c == ord(word[0]) - 97 else 1

        for i in range(1, n):
            ndp = [INF] * 26
            for cur in range(26):
                cost_cur = 0 if cur == ord(word[i]) - 97 else 1
                best = INF
                for prev in range(26):
                    if abs(cur - prev) > 1:          # not almost equal
                        if dp[prev] < best:
                            best = dp[prev]
                ndp[cur] = best + cost_cur
            dp = ndp

        return min(dp)
```

