class Solution:
    def countCompleteSubstrings(self, word: str, k: int) -> int:
        n = len(word)
        if k > n:
            return 0

        ans = 0
        # try every possible number of distinct characters in the substring
        for target_distinct in range(1, 27):  # 1 to 26
            cnt = [0] * 26
            left = 0
            distinct = 0          # number of chars with count > 0 in window
            exact_k = 0           # number of chars whose count == k

            for right in range(n):
                idx = ord(word[right]) - ord('a')
                if cnt[idx] == 0:
                    distinct += 1
                cnt[idx] += 1
                if cnt[idx] == k:
                    exact_k += 1
                if cnt[idx] == k + 1:
                    exact_k -= 1

                # shrink window if distinct exceeds target
                while distinct > target_distinct:
                    idx_l = ord(word[left]) - ord('a')
                    if cnt[idx_l] == k:
                        exact_k -= 1
                    if cnt[idx_l] == k + 1:
                        exact_k += 1
                    cnt[idx_l] -= 1
                    if cnt[idx_l] == 0:
                        distinct -= 1
                    left += 1

                # when window has exactly target_distinct distinct chars
                # and all of them appear exactly k times, count it
                if distinct == target_distinct and exact_k == target_distinct:
                    ans += 1

        return ans
```

