from typing import List

class Solution:
    def minimumCost(self, source: str, target: str, original: List[str], changed: List[str], cost: List[int]) -> int:
        INF = 10**15
        # 26 letters
        dist = [[INF] * 26 for _ in range(26)]
        for i in range(26):
            dist[i][i] = 0

        # set direct edges, keep minimal cost for duplicates
        for o, c, w in zip(original, changed, cost):
            u = ord(o) - 97
            v = ord(c) - 97
            if w < dist[u][v]:
                dist[u][v] = w

        # FloydWarshall for allpairs shortest paths
        for k in range(26):
            dk = dist[k]
            for i in range(26):
                dik = dist[i][k]
                if dik == INF:
                    continue
                di = dist[i]
                for j in range(26):
                    nd = dik + dk[j]
                    if nd < di[j]:
                        di[j] = nd

        total = 0
        for s_char, t_char in zip(source, target):
            if s_char == t_char:
                continue
            u = ord(s_char) - 97
            v = ord(t_char) - 97
            d = dist[u][v]
            if d >= INF:
                return -1
            total += d

        return total
```

