class Solution:
    def beautifulIndices(self, s: str, a: str, b: str, k: int) -> List[int]:
        n = len(s)
        la, lb = len(a), len(b)

        pos_a = []
        for i in range(n - la + 1):
            if s[i:i+la] == a:
                pos_a.append(i)

        pos_b = []
        for i in range(n - lb + 1):
            if s[i:i+lb] == b:
                pos_b.append(i)

        res = []
        for i in pos_a:
            left = i - k
            right = i + k
            idx = bisect_left(pos_b, left)
            if idx < len(pos_b) and pos_b[idx] <= right:
                res.append(i)

        return res
