from collections import deque

class Solution:
    def minimumOperationsToMakeEqual(self, x: int, y: int) -> int:
        if x == y:
            return 0

        # Upper bound for search space
        limit = max(x, y) * 2 + 20   # safe margin

        q = deque()
        q.append((x, 0))
        visited = {x}

        while q:
            cur, steps = q.popleft()
            if cur == y:
                return steps

            # increment
            nxt = cur + 1
            if nxt <= limit and nxt not in visited:
                visited.add(nxt)
                q.append((nxt, steps + 1))

            # decrement
            nxt = cur - 1
            if nxt >= 0 and nxt not in visited:
                visited.add(nxt)
                q.append((nxt, steps + 1))

            # divide by 5
            if cur % 5 == 0:
                nxt = cur // 5
                if nxt not in visited:
                    visited.add(nxt)
                    q.append((nxt, steps + 1))

            # divide by 11
            if cur % 11 == 0:
                nxt = cur // 11
                if nxt not in visited:
                    visited.add(nxt)
                    q.append((nxt, steps + 1))

        # Should never reach here for given constraints
        return -1
```

