from typing import List

class Solution:
    def countMatchingSubarrays(self, nums: List[int], pattern: List[int]) -> int:
        n = len(nums)
        m = len(pattern)
        count = 0
        # each subarray of length m+1 starts at i, ends at i+m
        for i in range(n - m):
            match = True
            for k in range(m):
                a, b = nums[i + k], nums[i + k + 1]
                p = pattern[k]
                if p == 1 and not (b > a):
                    match = False
                    break
                if p == 0 and not (b == a):
                    match = False
                    break
                if p == -1 and not (b < a):
                    match = False
                    break
            if match:
                count += 1
        return count
