class Solution:
    def maximumLength(self, s: str) -> int:
        # Gather runs of each character
        runs = {chr(c): [] for c in range(ord('a'), ord('z') + 1)}
        n = len(s)
        i = 0
        while i < n:
            j = i
            while j < n and s[j] == s[i]:
                j += 1
            runs[s[i]].append(j - i)
            i = j

        # Find the maximum possible run length
        max_len = max((max(lst) if lst else 0) for lst in runs.values())

        # Check lengths from longest to shortest
        for L in range(max_len, 0, -1):
            for ch, lst in runs.items():
                occ = 0
                for r in lst:
                    if r >= L:
                        occ += r - L + 1
                    if occ >= 3:
                        return L
        return -1
```

