class Solution:
    def numberOfPowerfulInt(self, start: int, finish: int, limit: int, s: str) -> int:
        # suffix value and its length
        suffix = int(s)
        L = len(s)
        base = 10 ** L

        # helper: ceil division for any integer a, positive b
        def ceil_div(a: int, b: int) -> int:
            return -((-a) // b)

        # range of possible prefixes p
        low = ceil_div(start - suffix, base)
        if low < 0:
            low = 0
        high = (finish - suffix) // base
        if high < 0 or low > high:
            return 0

        # count numbers p in [0, N] whose every digit <= limit
        from functools import lru_cache

        def count_upto(N: int) -> int:
            if N < 0:
                return 0
            digits = list(map(int, str(N)))
            n = len(digits)

            @lru_cache(None)
            def dfs(pos: int, tight: bool) -> int:
                if pos == n:
                    return 1  # a valid number constructed
                max_digit = digits[pos] if tight else 9
                # we can only use digits up to 'limit'
                max_digit = min(max_digit, limit)
                total = 0
                for d in range(0, max_digit + 1):
                    total += dfs(pos + 1, tight and d == digits[pos])
                return total

            return dfs(0, True)

        # answer = count(high) - count(low-1)
        return count_upto(high) - count_upto(low - 1)
```

