from typing import List

class Solution:
    def maximumSubarraySum(self, nums: List[int], k: int) -> int:
        min_prefix = {}          # value -> minimal prefix sum before an occurrence
        pref_before = 0          # sum of elements before current index
        max_sum = None

        for num in nums:
            pref_current = pref_before + num

            # possible start values that make a good subarray ending at current index
            for target in (num + k, num - k):
                if target in min_prefix:
                    cand = pref_current - min_prefix[target]
                    if max_sum is None or cand > max_sum:
                        max_sum = cand

            # store minimal prefix sum for this value (as a potential start)
            if num not in min_prefix or pref_before < min_prefix[num]:
                min_prefix[num] = pref_before

            pref_before = pref_current

        return 0 if max_sum is None else max_sum
