class Solution:
    def longestCommonPrefix(self, arr1: List[int], arr2: List[int]) -> int:
        # Build a set of all possible prefixes from numbers in arr1
        prefixes = set()
        for num in arr1:
            s = str(num)
            for i in range(1, len(s) + 1):
                prefixes.add(s[:i])

        max_len = 0
        for num in arr2:
            s = str(num)
            # check prefixes from longest to shortest
            for l in range(len(s), 0, -1):
                if s[:l] in prefixes:
                    if l > max_len:
                        max_len = l
                    break  # longest for this number found
        return max_len
