class Solution:
    def minimumTimeToInitialState(self, word: str, k: int) -> int:
        n = len(word)
        max_t = (n + k - 1) // k  # smallest t with t*k >= n
        for t in range(1, max_t):
            d = t * k
            if d < n and word[d:] == word[:n - d]:
                return t
        return max_t
