import sys
sys.setrecursionlimit(10000)

def solve() -> None:
    data = sys.stdin.read().strip().split()
    if not data:
        return
    it = iter(data)
    N = int(next(it))
    M = int(next(it))
    strings = [next(it) for _ in range(N)]

    # Precompute adjacency: distance exactly 1
    adj = [[False]*N for _ in range(N)]
    for i in range(N):
        si = strings[i]
        for j in range(N):
            if i == j:
                continue
            sj = strings[j]
            diff = 0
            for k in range(M):
                if si[k] != sj[k]:
                    diff += 1
                    if diff > 1:
                        break
            if diff == 1:
                adj[i][j] = True

    full_mask = (1 << N) - 1
    from functools import lru_cache

    @lru_cache(maxsize=None)
    def dfs(last: int, mask: int) -> bool:
        if mask == full_mask:
            return True
        for nxt in range(N):
            if not (mask >> nxt) & 1 and adj[last][nxt]:
                if dfs(nxt, mask | (1 << nxt)):
                    return True
        return False

    for start in range(N):
        if dfs(start, 1 << start):
            print("Yes")
            return
    print("No")

if __name__ == "__main__":
    solve()
```

