class Solution:
    def lastNonEmptyString(self, s: str) -> str:
        # frequency of each character
        freq = [0] * 26
        for ch in s:
            freq[ord(ch) - 97] += 1

        max_freq = max(freq)                     # maximum occurrence count
        occ = [0] * 26                           # occurrences seen so far
        result = []

        for ch in s:
            idx = ord(ch) - 97
            occ[idx] += 1
            # keep the character only if it is the last occurrence of a
            # character whose total count equals the maximum frequency
            if freq[idx] == max_freq and occ[idx] == max_freq:
                result.append(ch)

        return ''.join(result)
