import sys

def solve() -> None:
    S = sys.stdin.readline().strip()
    T = sys.stdin.readline().strip()

    allowed = set('atcoder')
    cntS = [0] * 26
    cntT = [0] * 26
    atS = atT = 0

    for ch in S:
        if ch == '@':
            atS += 1
        else:
            cntS[ord(ch) - 97] += 1

    for ch in T:
        if ch == '@':
            atT += 1
        else:
            cntT[ord(ch) - 97] += 1

    # letters that cannot be produced from '@' must match exactly
    for i in range(26):
        c = chr(i + 97)
        if c not in allowed and cntS[i] != cntT[i]:
            print("No")
            return

    # use '@' to compensate differences for allowed letters
    for i in range(26):
        c = chr(i + 97)
        if c in allowed:
            diff = cntS[i] - cntT[i]
            if diff > 0:
                if atT < diff:
                    print("No")
                    return
                atT -= diff
            elif diff < 0:
                need = -diff
                if atS < need:
                    print("No")
                    return
                atS -= need

    print("Yes")

if __name__ == "__main__":
    solve()
