from typing import List
from collections import Counter

class Solution:
    def maximumLength(self, nums: List[int]) -> int:
        freq = Counter(nums)
        max_val = max(nums)
        answer = 0

        # handle base = 1 separately (1^any = 1)
        cnt_one = freq.get(1, 0)
        if cnt_one:
            answer = cnt_one if cnt_one % 2 == 1 else cnt_one - 1

        for base in freq:
            if base == 1:
                continue

            # build the chain of successive squares that exist in the array
            powers = []
            cur = base
            while cur <= max_val and cur in freq:
                powers.append(cur)
                # avoid overflow for next square
                if cur > max_val // cur:
                    break
                cur = cur * cur

            best_len = 0
            # try each possible center position
            for c, center_val in enumerate(powers):
                if freq[center_val] < 1:
                    continue
                ok = True
                for i in range(c):
                    if freq[powers[i]] < 2:
                        ok = False
                        break
                if ok:
                    best_len = max(best_len, 2 * c + 1)

            answer = max(answer, best_len)

        # at least one element can always be taken
        return max(answer, 1)
```

