import sys

def solve() -> None:
    data = sys.stdin.read().strip().split()
    if not data:
        return
    S = data[0].strip()
    N = int(data[1])
    L = len(S)

    # If N is large enough to cover all possible values of length L
    if N >= (1 << L):
        # maximum value: set every '?' to 1, keep fixed bits
        ans = 0
        for ch in S:
            ans = (ans << 1) | (1 if ch != '0' else 0)
        print(ans)
        return

    # Bits of N in L-bit representation (most significant first)
    n_bits = [(N >> (L - 1 - i)) & 1 for i in range(L)]

    # dp[i][less] : feasibility from position i (0..L) with flag less (0 equal so far, 1 already less)
    dp = [[False, False] for _ in range(L + 1)]
    dp[L][0] = dp[L][1] = True

    for i in range(L - 1, -1, -1):
        allowed = []
        if S[i] == '?':
            allowed = [0, 1]
        elif S[i] == '0':
            allowed = [0]
        else:  # '1'
            allowed = [1]

        for lessPrev in (0, 1):
            feasible = False
            for b in allowed:
                if lessPrev == 0:
                    if b > n_bits[i]:
                        continue
                    lessNext = 1 if b < n_bits[i] else 0
                else:
                    lessNext = 1
                if dp[i + 1][lessNext]:
                    feasible = True
                    break
            dp[i][lessPrev] = feasible

    if not dp[0][0]:
        print(-1)
        return

    # Reconstruct the maximum value
    lessPrev = 0
    ans = 0
    for i in range(L):
        allowed = []
        if S[i] == '?':
            allowed = [1, 0]  # try larger first
        elif S[i] == '0':
            allowed = [0]
        else:
            allowed = [1]

        chosen = None
        for b in allowed:
            if lessPrev == 0:
                if b > n_bits[i]:
                    continue
                lessNext = 1 if b < n_bits[i] else 0
            else:
                lessNext = 1
            if dp[i + 1][lessNext]:
                chosen = b
                lessPrev = lessNext
                break
        ans = (ans << 1) | chosen

    print(ans)


if __name__ == "__main__":
    solve()
