```python
from typing import List

class Solution:
    def minimumArrayLength(self, nums: List[int]) -> int:
        n = len(nums)
        if n == 1:
            return 1

        mn = min(nums)
        # check if every element is a multiple of the minimum element
        all_mult = all(x % mn == 0 for x in nums)

        if not all_mult:
            return 1

        # all numbers are multiples of the minimum
        if n == 2:
            # two equal multiples can be reduced to a single zero
            return 1

        # for larger n the minimal length follows the pattern described
        return max(2, n // 2)
