class Solution:
    MOD = 10**9 + 7

    def count(self, num1: str, num2: str, min_sum: int, max_sum: int) -> int:
        from functools import lru_cache

        def count_le(bound: str) -> int:
            n = len(bound)

            @lru_cache(None)
            def dfs(pos: int, tight: int, s: int) -> int:
                if pos == n:
                    return 1 if min_sum <= s <= max_sum else 0
                limit = int(bound[pos]) if tight else 9
                total = 0
                for d in range(limit + 1):
                    ntight = tight and (d == limit)
                    ns = s + d
                    if ns > max_sum:  # pruning
                        continue
                    total = (total + dfs(pos + 1, ntight, ns)) % self.MOD
                return total

            return dfs(0, 1, 0)

        def subtract_one(s: str) -> str:
            # assume s represents a positive integer
            lst = list(s)
            i = len(lst) - 1
            while i >= 0 and lst[i] == '0':
                lst[i] = '9'
                i -= 1
            if i >= 0:
                lst[i] = chr(ord(lst[i]) - 1)
            # remove leading zeros
            res = ''.join(lst).lstrip('0')
            return res if res else '0'

        total2 = count_le(num2)
        num1_minus = subtract_one(num1)
        total1 = count_le(num1_minus) if num1_minus != '0' else 0
        return (total2 - total1) % self.MOD

