            while x > 1:
                p = spf[x]
                primes.add(p)
                while x % p == 0:
                    x //= p
            return primes

        parent = list(range(n))
        rank = [0] * n

        def find(x):
            while parent[x] != x:
                parent[x] = parent[parent[x]]
                x = parent[x]
