            res = 1
            temp = x
            for p in primes:
                if p * p > temp:
                    break
                cnt = 0
                while temp % p == 0:
                    temp //= p
                    cnt ^= 1  # keep only parity
                if cnt:
                    res *= p
            if temp > 1:  # remaining prime factor
                res *= temp
            return res

        group_sum = {}
        max_single = 0
        for val in nums:
            sf = square_free_part(val)
            group_sum[sf] = group_sum.get(sf, 0) + val
            if val > max_single:
