class Solution:
    def minimumOperationsToMakeEqual(self, x: int, y: int) -> int:
        if x == y:
            return 0
        # Upper bound for search space
        limit = max(x, y) * 2 + 100
        visited = set()
        q = deque()
        q.append((x, 0))
        visited.add(x)
        while q:
            cur, dist = q.popleft()
            # Generate neighbors
            nxts = []
            if cur % 11 == 0:
                nxts.append(cur // 11)
            if cur % 5 == 0:
                nxts.append(cur // 5)
