class Solution:
    def minimumAddedCoins(self, coins: List[int], target: int) -> int:
        coins.sort()
        reach = 0          # maximum sum achievable with current coins
        added = 0          # number of coins added

        for coin in coins:
            # If the current coin is too large to extend the reachable range,
            # add the smallest missing coin (reach + 1) until we can use this coin.
            while coin > reach + 1 and reach < target:
                reach += reach + 1
                added += 1
            if coin <= reach + 1:
                reach += coin
            if reach >= target:
                return added

        # After processing all original coins, we may still need to add coins
