class Solution:
    def countCompleteSubstrings(self, word: str, k: int) -> int:
        n = len(word)
        arr = [ord(c) - 97 for c in word]
        # Precompute violation array
        violation = [0] * n
        for i in range(1, n):
            if abs(arr[i] - arr[i - 1]) > 2:
                violation[i] = 1
        ans = 0
        for m in range(1, 27):
            L = m * k
            if L > n:
                break
            counts = [0] * 26
            distinct_count = 0
            good_chars = 0
            over_k_count = 0
            # initial window [0, L-1]
            violation_count = sum(violation[1:L]) if L > 1 else 0
            for i in range(L):
                c = arr[i]
                prev = counts[c]
                counts[c] += 1
                if prev == 0:
                    distinct_count += 1
                if counts[c] == k:
                    good_chars += 1
                elif counts[c] == k + 1:
                    over_k_count += 1
            if over_k_count == 0 and violation_count == 0 and good_chars == m:
                ans += 1
            # slide window
            for r in range(L, n):
                l = r - L
                # remove char at l
                c_remove = arr[l]
                prev = counts[c_remove]
                counts[c_remove] -= 1
                if prev == k + 1:
                    over_k_count -= 1
                    good_chars += 1
                elif prev == k:
                    good_chars -= 1
                elif prev == 1:
                    distinct_count -= 1
                # add char at r
